import { FaTimes } from 'react-icons/fa'
import Tasks from './Tasks'

const Task = ({ task,onDelete }) => {
    return (
        <div className='task'>
            <h3>{task.text} <FaTimes style={{ color:'red',cursor:'pointer', }} onClick={() => onDelete,Tasks.id}/></h3>
            <p>{task.day}</p>
        </div>
    )
}

export default Task
